import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { User } from "../common/User";
import { Content } from "../common/Content";
import Consts from "../Consts";
import commonStyles from "../common/common.module.scss";
import * as $ from 'jquery';
import { Helmet } from "react-helmet";
import logo from '../images/logoproject.png'; // with import


import {
  Stack,
  PrimaryButton,
  TextField,
  IIconProps,
  Modal,
  IconButton,
  Label,
  DayOfWeek,
  DatePicker,
  ComboBox,
  IComboBoxOption,
  Spinner,
  SpinnerSize,
  PivotItem,
  Pivot,
  Dropdown,
  IDropdownStyles,
  IDropdownOption,
  IContextualMenuItem,
  getIconClassName,
  initializeIcons,
  StackItem,
  Toggle,
  DefaultButton,
} from "office-ui-fabric-react";
import 'datatables.net';
import 'datatables.net-responsive';
import 'datatables.net-buttons';
import 'datatables.net-select';
import 'datatables.net-buttons/js/buttons.html5';
import 'datatables.net-buttons/js/buttons.print';
import ReactDOM from "react-dom";
import { Button, CircularProgress } from "@material-ui/core";


interface ILoginRegisterProps {
  submit: (
    userLogged: User
  ) => Promise<boolean>;
}
var showErrors = false;
interface RegisterUser {
  name: string;
  surname: string;
  description: string;
  password: string;
  email: string;


}
interface ILoginRegisterState {
  email: string;
  password: string;
  loggin: boolean;
  loadingCambio: boolean;
  confpassword: string;
  name: string;
  surname: string;
  description: string;
  errorName: string;
  errorSurname: string;
  errorDesc: string;
  errorPass: string;
  errorEmail: string;
  errorConf: string;

}

export default class LoginRegister extends React.Component<
  ILoginRegisterProps,
  ILoginRegisterState
> {

  public userActual: RegisterUser;
  public tableContent;
  constructor(props: ILoginRegisterProps, state: ILoginRegisterState) {
    super(props);
    this.state = {
      email: "",
      loggin: true,
      password: "",
      confpassword: "",
      name: "",
      loadingCambio: false,
      surname: "",
      description: "",
      errorName: "",
      errorSurname: "",
      errorDesc: "",
      errorPass: "",
      errorEmail: "",
      errorConf: "",
    };
    initializeIcons();

  }

  public checkForm(submit?: boolean) {
    let allOK = true;
    if (showErrors) {


      if (this.state.name.trim()) {
        this.setState({ errorName: "" })
      } else {
        this.setState({ errorName: "Rellene este campo" });
        allOK = false;
      }
      if (this.state.surname.trim()) {
        this.setState({ errorSurname: "" })
      } else {
        this.setState({ errorSurname: "Rellene este campo" });
        allOK = false;
      }
      if (this.state.description.trim()) {
        this.setState({ errorDesc: "" })
      } else {
        this.setState({ errorDesc: "Rellene este campo" });
        allOK = false;
      }
      if (this.state.password.trim()) {
        this.setState({ errorPass: "" })
      } else {
        this.setState({ errorPass: "Rellene este campo" });
        allOK = false;
      }
      if (this.state.password === this.state.confpassword) {
        this.setState({ errorConf: "" })
      } else {
        this.setState({ errorConf: "Las contraseÃ±as no coinciden" });
        allOK = false;
      }
      if (this.state.email.trim()) {
        this.setState({ errorEmail: "" })
      } else {
        this.setState({ errorEmail: "Rellene este campo" });
        allOK = false;
      }
    }
    if (submit) {
      return allOK;
    }
  }
  public componentDidMount() {
    showErrors = false;


  }
  public component
  public componentDidUpdate(
    prevProps: Readonly<ILoginRegisterProps>,
    prevState: Readonly<ILoginRegisterState>,
    snapshot?: any
  ): void {
    if (this.state.loadingCambio) {
      setTimeout(() => {
        this.setState({
          loggin: !this.state.loggin,
          loadingCambio: false
        })
      }, 1000);
    }
    if (
      prevState.name != this.state.name ||
      prevState.surname != this.state.surname ||
      prevState.description != this.state.description ||
      prevState.password != this.state.password ||
      prevState.confpassword != this.state.confpassword ||
      prevState.email != this.state.email) {
      this.checkForm(false);

    }
  }
  public async registerUser() {

    this.userActual = {
      email: this.state.email,
      name: this.state.name,
      surname: this.state.surname,
      description: this.state.description,
      password: this.state.password
    }

    const util = JSON.stringify(this.userActual);
    console.log(util);
    // Â¡Y enviarlo!
    const respuesta = await fetch(`http://localhost:8080/registerUser.php`, {
      mode: 'cors',
      method: "POST",
      body: util,
    });
    const exitoso = await respuesta.json();
    console.log(exitoso);
    if (exitoso) {
      // toast('Videojuego guardado ðŸŽ®', {
      //     position: "top-left",
      //     autoClose: 2000,
      //     hideProgressBar: false,
      //     closeOnClick: true,
      //     pauseOnHover: true,
      //     draggable: true,
      //     progress: undefined,
      // });
      console.log("exito")

    } else {
      // toast.error("Error guardando. Intenta de nuevo");
    }
  }

  render(): React.ReactElement<ILoginRegisterProps> {

    return (
      <div
        className={commonStyles.logginRgister}
      >
        <Helmet>
          <style>{'body { background-color: #ccc; }'}</style>
        </Helmet>
        <Stack
