import React, { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { User } from "../common/User";
import { Content } from "../common/Content";
import Consts from "../Consts";
import commonStyles from "../common/common.module.scss";
import * as $ from 'jquery';
import emptypicture from "../images/notpicture.png"

import {
    
    Stack,
    PrimaryButton,
    TextField,
    IIconProps,
    Modal,
    IconButton,
    Label,
    DayOfWeek,
    DatePicker,
    ComboBox,
    IComboBoxOption,
    Spinner,
    SpinnerSize,
    PivotItem,
    Pivot,
    Dropdown,
    IDropdownStyles,
    IDropdownOption,
    IContextualMenuItem,
    getIconClassName,
    initializeIcons,
    Rating,
    RatingSize,
    DefaultButton,
    Persona,
    IPersonaSharedProps,
    PersonaSize,
} from "office-ui-fabric-react";
import { Helmet } from "react-helmet";

import 'datatables.net';
import 'datatables.net-responsive';
import 'datatables.net-buttons';
import 'datatables.net-select';
import 'datatables.net-buttons/js/buttons.html5';
import 'datatables.net-buttons/js/buttons.print';
import ReactDOM from "react-dom";
import ContentCard from "./ContentCard";
import { getLeadingCommentRanges } from "typescript";
import { common } from "@material-ui/core/colors";
import NewComment from "./NewComment";



function ShowComment(props) {

    // const [rating, setRating] = useState(props.location.state.item.rating);

    const [user, setUser] = useState(props.users[props.item.user_id]);



   const  persona: IPersonaSharedProps = {
        imageUrl: user?user.profileImage:emptypicture,
        imageInitials: '',
        text: user?""+user.name+" "+user.surname:"Undefined" ,
      
      };
    if (props.content_id != props.item.content_id)
        return (<></>);


    return (

        <>
            <div
                className={commonStyles.contentCard}
                style={{ width: "100%", padding: "5px" }}

            >
                <Stack
                    horizontal
                    horizontalAlign="space-between"
                >
                    <div
                        style={{ width: "65%", padding: "5px" }}
                    >

                        {/* AÃ‘ADIR PERSONA O PARA MOSTRAR EL USUARIO, FOTO REDONDA Y NOMBRE CON CLICK PROPS.CLICKUSER(ID) */}
                        <DefaultButton
                            style={{ width: "70%" }}
                            onClick={() => {
                                // props.submit(comment, rating);
                            }}>
                            <Persona
                                {...persona}
                                text={persona.text}
                                size={PersonaSize.size32}
                                hidePersonaDetails={false}
                                imageAlt={persona.text}
                            />
                        </DefaultButton>

                    </div>
                    <div
                        style={{ width: "65%", padding: "5px" }}

                    >
                        {props.item.comment}
                    </div>
                    <div
                        style={{ width: "15%" }}
                    >
                        <Rating
                            min={0}
                            max={5}
                            size={RatingSize.Large}
                            rating={props.item.rating}

                            readOnly
                            // value={2}

                            ariaLabelFormat="{0} of {1} stars"
                        />

                    </div>


                </Stack>
            </div>

        </>

    );
}


export default ShowComment;
