import React, { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { User } from "../common/User";
import { Content } from "../common/Content";
import Consts from "../Consts";
import commonStyles from "../common/common.module.scss";
import * as $ from 'jquery';
import {
    Stack,
    PrimaryButton,
    TextField,
    IIconProps,
    Modal,
    IconButton,
    Label,
    DayOfWeek,
    DatePicker,
    ComboBox,
    IComboBoxOption,
    Spinner,
    SpinnerSize,
    PivotItem,
    Pivot,
    Dropdown,
    IDropdownStyles,
    IDropdownOption,
    IContextualMenuItem,
    getIconClassName,
    initializeIcons,
    Rating,
} from "office-ui-fabric-react";
import { Helmet } from "react-helmet";

import 'datatables.net';
import 'datatables.net-responsive';
import 'datatables.net-buttons';
import 'datatables.net-select';
import 'datatables.net-buttons/js/buttons.html5';
import 'datatables.net-buttons/js/buttons.print';
import ReactDOM from "react-dom";
import ContentCard from "./ContentCard";
import { getLeadingCommentRanges } from "typescript";
import { common } from "@material-ui/core/colors";
import NewComment from "./NewComment";
import emptypicture from "../images/notpicture.png"




function Profile(props) {

    // const [rating, setRating] = useState(props.location.state.item.rating);



    return (

        <>
        <Stack
        horizontal
        horizontalAlign="space-evenly"
        style={{ width: "90%", margin: "0 auto" }}
    >
        <div
            style={{ padding: "30px", width: "30%", border: "1px solid black", margin: "5px" }}
            className={commonStyles.contentCard}>
            {/* */}
            {/* Probamos imagen vacía o si nos deja subir foto guay */}
            <img style={{ width: "100%", minHeight: "350px", objectFit: "cover", display: "block" }} src={props.location.state.user.profileImage?props.location.state.user.profileImage:emptypicture} alt="" /> 
            <Stack
                horizontal
                horizontalAlign="center"
                style={{ marginTop: "30px" }}
            >
              {/* media de rating y numero comentarios? */}
            </Stack>
        </div>
        <div
            style={{ padding: "30px", width: "70%", border: "1px solid black", margin: "5px" }}
            className={commonStyles.contentCard}>
            <span style={{ display: "block", marginBottom: "15px" }}>
                <h3>{props.location.state.user.name + " "+ props.location.state.user.surname}</h3>
            </span>
            <hr />
            <span style={{ display: "block", marginBottom: "15px" }}>
                <span className={commonStyles.detailLabel}>Descripción:</span> <span className={commonStyles.detailField}>{" " + props.location.state.user.description}</span>
            </span>
            <hr />

          {/* if is same user boton de edición y abrimos el panel */}
        </div>


    </Stack>
        </>

    );
}


export default Profile;
