import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { User } from "../common/User";
import { Content } from "../common/Content";
import Consts from "../Consts";
import commonStyles from "../common/common.module.scss";
import * as $ from 'jquery';
import {
  Stack,
  PrimaryButton,
  TextField,
  IIconProps,
  Modal,
  IconButton,
  Label,
  DayOfWeek,
  DatePicker,
  ComboBox,
  IComboBoxOption,
  Spinner,
  SpinnerSize,
  PivotItem,
  Pivot,
  Dropdown,
  IDropdownStyles,
  IDropdownOption,
  IContextualMenuItem,
  getIconClassName,
  initializeIcons,
  StackItem,
  Toggle,
} from "office-ui-fabric-react";
import 'datatables.net';
import 'datatables.net-responsive';
import 'datatables.net-buttons';
import 'datatables.net-select';
import 'datatables.net-buttons/js/buttons.html5';
import 'datatables.net-buttons/js/buttons.print';
import ReactDOM from "react-dom";


interface ILoginRegisterProps {
  submit: (
    userLogged: User
  ) => Promise<boolean>;
}

interface ILoginRegisterState {
  email: string;
  password: string;
  loggin: boolean;
  confpassword:string;
}

export default class LoginRegister extends React.Component<
  ILoginRegisterProps,
  ILoginRegisterState
> {

  public userActual;
  public tableContent;
  constructor(props: ILoginRegisterProps, state: ILoginRegisterState) {
    super(props);
    this.state = {
      email: "",
      loggin: true,
      password: "",
      confpassword:""
    };
    initializeIcons();

  }

  public componentDidMount() {


  }
  public component
  public componentDidUpdate(
    prevProps: Readonly<ILoginRegisterProps>,
    prevState: Readonly<ILoginRegisterState>,
    snapshot?: any
  ): void { }

  public _onChange(ev: React.MouseEvent<HTMLElement>, checked?: boolean) {
          this.setState({loggin:!this.state.loggin})
    }

  render(): React.ReactElement<ILoginRegisterProps> {

    return (
      <Stack>

        {this.state.loggin &&
          <Stack padding={10}
            horizontal
            horizontalAlign="center"
            style={{ width: "100%" }}
            tokens={{ childrenGap: 10 }}>
            <div className={commonStyles.formLoggin}>
              <TextField
                label={"Email"}
                onChange={(
                  ev: React.SyntheticEvent<HTMLElement, Event>,
                  email: string
                ) => {
                  this.setState({ email: email });
                }}
                value={this.state.email ? this.state.email : ""}
                required={true}
                autoAdjustHeight
              />
              {/* <span
                 className={commonStyles.errorEmail}
                 style={{ display: this.state.errorEmail ? "block" : "none" }}
               >
                 {this.state.errorEmail}
               </span> */}
              <TextField
                label="Contraseña"
                type="password"
                onChange={(
                  ev: React.SyntheticEvent<HTMLElement, Event>,
                  password: string
                ) => {
                  this.setState({ password: password });
                }}
                value={this.state.password ? this.state.password : ""}
                canRevealPassword
              />
            </div>
          </Stack>}
        {!this.state.loggin &&
          <Stack padding={10}
            horizontal
            horizontalAlign="center"
            style={{ width: "100%" }}
            tokens={{ childrenGap: 10 }}>
            <div className={commonStyles.formLoggin}>
              <TextField
                label={"Email"}
                onChange={(
                  ev: React.SyntheticEvent<HTMLElement, Event>,
                  email: string
                ) => {
                  this.setState({ email: email });
                }}
                value={this.state.email ? this.state.email : ""}
                required={true}
                autoAdjustHeight
              />
              {/* <span
                 className={commonStyles.errorEmail}
                 style={{ display: this.state.errorEmail ? "block" : "none" }}
               >
                 {this.state.errorEmail}
               </span> */}
              <TextField
                label="Contraseña"
                type="password"
                onChange={(
                  ev: React.SyntheticEvent<HTMLElement, Event>,
                  password: string
                ) => {
                  this.setState({ password: password });
                }}
                value={this.state.password ? this.state.password : ""}
                canRevealPassword
              />
               <TextField
                label="Confirmar contraseña"
                type="password"
                onChange={(
                  ev: React.SyntheticEvent<HTMLElement, Event>,
                  confpassword: string
                ) => {
                  this.setState({ confpassword: confpassword });
                }}
                value={this.state.password ? this.state.password : ""}
                canRevealPassword
              />
            </div>
          </Stack>}
        <Stack
        horizontal
        horizontalAlign="space-between"
        style={{width:"250px"}}>
          <Label>Registrarse</Label>
          <Toggle label="" defaultChecked onText="" offText="" onChange={this._onChange} />
          <Label>Loggin</Label>
        </Stack>
      </Stack>
    );
  }
}
