import React, { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { User } from "../common/User";
import { Content } from "../common/Content";
import Consts from "../Consts";
import commonStyles from "../common/common.module.scss";
import * as $ from 'jquery';
import {
    Stack,
    PrimaryButton,
    TextField,
    IIconProps,
    IconButton,
    Label,
    DayOfWeek,
    DatePicker,
    ComboBox,
    IComboBoxOption,
    Spinner,
    SpinnerSize,
    PivotItem,
    Pivot,
    Dropdown,
    IDropdownStyles,
    IDropdownOption,
    IContextualMenuItem,
    getIconClassName,
    initializeIcons,
    Rating,
    RatingSize,
    DefaultButton,
    mergeStyleSets,
} from "office-ui-fabric-react";
import { Helmet } from "react-helmet";

import 'datatables.net';
import 'datatables.net-responsive';
import 'datatables.net-buttons';
import 'datatables.net-select';
import 'datatables.net-buttons/js/buttons.html5';
import 'datatables.net-buttons/js/buttons.print';
import ReactDOM from "react-dom";
import ContentCard from "./ContentCard";
import { getLeadingCommentRanges } from "typescript";
import { common } from "@material-ui/core/colors";
import { Box, Typography, Modal, Button } from "@material-ui/core";



function FormSugg(props) {

    const [title, setTitle] = useState("")
    const [year, setYear] = useState("")
    const [platform, setPlatform] = useState("")

    const [rating, setRating] = useState(0)
    const [open, setOpen] = useState(true);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);


    const style = {
        position: 'absolute' as 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: 600,
        bgcolor: 'background.paper',
        border: '2px solid #000',
        boxShadow: 24,
        p: 4,
    };
    return (
        <>
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-year"
            >
                <Box sx={style}>
                    <h3 id="modal-modal-title">
                        Crear sugerencia
                    </h3>
                    <div id="modal-modal-year" >
                        <Stack>

                            <Stack>
                                <TextField
                                    label={"Titulo"}
                                    onChange={(
                                        ev: React.SyntheticEvent<HTMLElement, Event>,
                                        title: string
                                    ) => {
                                        setTitle(title);
                                    }}
                                    value={title ? title : ""}
                                    required={true}
                                    autoAdjustHeight />
                                {/* <span
        className={commonStyles.errorSpan}
        style={{ display: this.state.errorName ? "block" : "none" }}
    >
        {this.state.errorName}
    </span> */}
                            </Stack>
                            <Stack>
                                <TextField
                                    label={"Plataforma"}
                                    onChange={(
                                        ev: React.SyntheticEvent<HTMLElement, Event>,
                                        platform: string
                                    ) => {
                                        setPlatform(platform);
                                    }}
                                    value={platform ? platform : ""}
                                    required={true}
                                    autoAdjustHeight />
                                {/* <span
        className={commonStyles.errorSpan}
        style={{ display: this.state.errorplatform ? "block" : "none" }}
    >
        {this.state.errorplatform}
    </span> */}
                            </Stack>
                            <Stack>
                                <TextField
                                    label={"Año"}
                                    onChange={(
                                        ev: React.SyntheticEvent<HTMLElement, Event>,
                                        year: string
                                    ) => {
                                        console.log(year);
                                        setYear(year);
                                    }}
                                    value={year ? year : ""}
                                    autoAdjustHeight
                                />
                                {/* <span
        className={commonStyles.errorSpan}
        style={{ display: this.state.errorDesc ? "block" : "none" }}
    >
        {this.state.errorDesc}
    </span> */}
                            </Stack>
                            <Label>Imagen de perfil</Label>
                            <Button
                                variant="contained"
                                component="label"
                                style={{ margin: "1px" }}
                            >
                                <input
                                    type="file"
                                    style={{ padding: "10px" }}
                                />
                            </Button>
                        </Stack>                    </div>
                </Box>
            </Modal>

        </>
    );
}
export default FormSugg;
