} from "office-ui-fabric-react";
import 'datatables.net';
import 'datatables.net-responsive';
import 'datatables.net-buttons';
import 'datatables.net-select';
import 'datatables.net-buttons/js/buttons.html5';
import 'datatables.net-buttons/js/buttons.print';
import ReactDOM from "react-dom";


interface ILoginRegisterProps {
  submit: (
    userLogged: User
  ) => Promise<boolean>;
}

interface ILoginRegisterState {
  email:string;
  password:string;
}

export default class LoginRegister extends React.Component<
  ILoginRegisterProps,
  ILoginRegisterState
> {

  public userActual;
  public tableContent;
  constructor(props: ILoginRegisterProps, state: ILoginRegisterState) {
    super(props);
    this.state = {
      email:"",
      password:""
    };
    initializeIcons();

  }

  public componentDidMount() {


  }
  public component
  public componentDidUpdate(
    prevProps: Readonly<ILoginRegisterProps>,
    prevState: Readonly<ILoginRegisterState>,
    snapshot?: any
  ): void { }



  render(): React.ReactElement<ILoginRegisterProps> {

    return (
      <Stack padding={10} tokens={{ childrenGap: 10 }}>
        <div>
        <TextField
                  label={"Email"}
                  onChange={(
                    ev: React.SyntheticEvent<HTMLElement, Event>,
                    email: string
                  ) => {
                    this.setState({ email: email });
                  }}
                  value={this.state.email ? this.state.email : ""}
                  required={true}
                  autoAdjustHeight
                />
                {/* <span
                  className={commonStyles.errorEmail}
                  style={{ display: this.state.errorEmail ? "block" : "none" }}
                >
                  {this.state.errorEmail}
                </span> */}
                  <TextField
          label="ContraseÃ±a"
          type="password"
          onChange={(
            ev: React.SyntheticEvent<HTMLElement, Event>,
            password: string
          ) => {
            this.setState({password: password });
          }}
          value={this.state.password ? this.state.password : ""}
          canRevealPassword
        />
        </div>
      </Stack>
    );
  }
}
